import React, { useEffect, useState } from 'react'
import { Link } from "react-router-dom";

export default function Restaurent() {
    let [restaurentdata , setRestaurentdata]=useState([])


    let getproducts = ()=>{ 
     axios.get('http://localhost:5100/restaurent')
     .then((res)=>{
       return res.data
     })
     .then((finalres)=>{
       setRestaurentdata(finalres.data)
     })
    }
    useEffect(()=>{
       getproducts();
    },[])
  return (
    <>
        <div className="container-fluid">
            <div className="container">
                <div className="row gy-5">
                    <h2>All Restaurent</h2>
                    <div className='col-lg-4 col-md-6 '>
                    <Link to={'/restaurent-detail'} className='text-decoration-none text-black'>
                        <div className=''><img className='img-fluid rounded-4' src="https://media-cdn.tripadvisor.com/media/photo-s/1a/7d/b9/a8/guests-having-dinner.jpg" alt="" /></div>
                        <div className='p-1'>
                        <div className='d-flex justify-content-between mb-1'>
                        <span className='fw-bold'>KANKARIYA HERITAGE </span>
                            <span className='bg-success fw-bold  px-2 rounded-2 text-white '>4.7*</span>
                        </div>
                        <div className='d-flex justify-content-between  mb-1'>
                            <span className='fs-6  fw-light'>North Indian , Mughlai , Raj..</span>
                            <span className='fw-light' >1750 for two</span>
                        </div>
                        <div className='d-flex justify-content-between'>
                            <span className='fw-lighter'>Gulab Sagar , Jodhpur</span>
                            <span className='fw-bold'>8.9 km</span>
                        </div>
                        </div>
                    </Link>
                        <hr />
                    </div>
                </div>
            </div>
        </div>
    </>
  )
}
