import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link } from "react-router-dom";

export default function Restaurent() {
    let [restaurentdata, setRestaurentdata] = useState([])


    let getproducts = () => {
        axios.get('http://localhost:5100/restaurent')
            .then((res) => {
                return res.data
            })
            .then((finalres) => {
                setRestaurentdata(finalres.data)
            })
    }
    useEffect(() => {
        getproducts();
    }, [])
    return (
        <>
            <div className="container-fluid">
                <div className="container">
                    <div className="row gy-5">
                        <h2>Top Restaurents in Jodhpur</h2>
                        {
                            restaurentdata.length >= 1
                                ?
                                restaurentdata.map((ritems, index) => {
                                    return <Restaurents rdata={ritems.dish} />
                                })
                                :
                                'No Data Found'
                        }
                    </div>
                </div>
            </div>
        </>
    )
}

function Restaurents({rdata}) {
    return (
        <div className='col-lg-4 col-md-6 '>
            <Link to={'/restaurent-detail'} className='text-decoration-none text-black'>
                <div className=''><img className='img-fluid rounded-4' src={rdata.image} alt="" /></div>
                <div className='p-1'>
                    <div className='d-flex justify-content-between mb-1'>
                        <span className='fw-bold'>{rdata.name}</span>
                        <span className='bg-success fw-bold  px-2 rounded-2 text-white '>{rdata.rating}</span>
                    </div>
                    <div className='d-flex justify-content-between  mb-1'>
                        <span className='fs-6  fw-light'> {rdata.dish} </span>
                        <span className='fw-light' > {rdata.price} </span>
                    </div>
                    <div className='d-flex justify-content-between'>
                        <span className='fw-lighter'> {rdata.location} </span>
                        <span className='fw-bold'> {rdata.distance} </span>
                    </div>
                </div>
            </Link>
            <hr />
        </div>
    )
}
