import React, { createContext, useState } from 'react'
let dishContext=createContext()

export default function MainContext(props) {
    let [modalDish, setModalDish] = useState({
        dishname: "",
        dishprice: "",
        dishdescription: "",
        dishrating: "",
        image: ""
    })
  return (
    <dishContext.Provider value={
        {
            modalDish,
            setModalDish
        }
    }>
        {props.children}
    </dishContext.Provider>
  )
}
export {dishContext}
